import React from 'react'
import Backdrop from '../UI/Backdrop/Backdrop'

export default function ConfirmBox() {
  return (
    <Backdrop>
        <div>
            <p>该操作不可恢复，确认清空购物车吗？</p>
            <button>取消</button>
            <button>确认</button>
        </div>

    </Backdrop>
  )
}
