            {number !==0 && <Minus number={number} onSubtract={subHandler}/>}
            <button onClick={add} className={styles.add}>
              <FontAwesomeIcon icon={faPlus}/>
            </button>
          </div>      
        </div> */}


      </div>
    </div>

  )
}
