import React from 'react'
import './MealsList.css'
import MealItem from '../UI/MealItem/MealItem.js'

export default function MealsList(props) {


  const mealItems = props.mealsData.map(item => <MealItem
                                                        id={item.id}
                                                        item={item}
                                                        number={props.number}
                                                        addSum={(item)=>{props.addSum(item)}}
                                                        subSum={(item)=>{props.subSum(item)}}/>)


  return (

    <div className='meals' >
      {mealItems}

    </div>
  )
}
